import React from "react";
import './Login.css';
import { loginUser, getUserProfile } from "../api";

const Login = ({setUsername, username, setPassword, password, navigate, setToken, setUserProfile}) => {

    const handleSubmit = async () => {
        const results = await loginUser(username, password);

        if (results.success) {
            setToken(results.data.token)
            window.localStorage.setItem('token', results.data.token) 
            const profileResults = await getUserProfile(results.data.token)
            let my
            setUserProfile(profileResults.data)
            console.log(profileResults.data)
            navigate('/profile') 
        } else {
            window.alert ('Username or Password is incorrect, please try again.')
            let form = document.querySelector('form')
            form.reset()
        }
    }

    return (
        <form onSubmit = {(event)=> {
            event.preventDefault();
                handleSubmit()
            }
        }>
            <input className="usernameEntry"
            type='text'
            placeholder="Enter Username"
            onChange={(event) => setUsername(event.target.value)}>
            </input>
            <input className="passwordEntry"
            type='password'
            placeholder="Enter Password"
            onChange={(event) => setPassword(event.target.value) }>
            </input>


            <button type='submit'>Submit</button>
        </form>
    )
}


export default Login;
