import React, { useState, useEffect } from "react";
import ReactDOM from 'react-dom/client';
import { getUserProfile } from "./api";
import {
    Route, BrowserRouter, Routes, useNavigate
} from 'react-router-dom'
import "./style.css";




//for below will automatically look for index.js since working with javascript
import {
    Navbar,
    Posts,
    Profile,
    Home,
    Register,
    Login,
  } from './Components';
  import {
    getPosts,
  } from './api';

/*
Components needed for this project:
Login
Registration
Posts
AddPost
Profile
Navbar

*/

const App = () => {
    const [ posts, setPosts ] = useState([]);
    const [ token, setToken ] = useState('');
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [userProfile, setUserProfile] = useState({})
    const [open, setOpen] = useState(false)
    const navigate = useNavigate();

    const logout = () => {
        window.localStorage.removeItem('token');
        setToken('') 
        setUserProfile({});
    }

    console.log (userProfile)

    const fetchPosts = async() => {
        const results = await getPosts(token)
        setPosts(results.data.posts)
    }
    const getProfile = async() => {
        const storedToken = window.localStorage.getItem('token')

        if (!token) {

            setToken(storedToken);
            return
        }
        const myProfile = await getUserProfile(token)
        if (myProfile.success) {
            setUserProfile(myProfile.data);
        } else {
            console.log(myProfile.error.message);
        }
    }


    useEffect (() => {
        fetchPosts()
    },[token])

    useEffect ( () => {
        getProfile();
    }, [token])

    return (
        <div>
            <Navbar logout={logout}/>
            <Routes>
                <Route path='/' element={<Home />} />
                <Route path='/posts' element={<Posts posts={posts} />} />
                <Route path='/profile' element={<Profile userProfile={userProfile} />} />
                <Route path='/register' element={<Register 
                    open={open} 
                    setOpen={setOpen} 
                    setUserProfile={setUserProfile} 
                    setToken={setToken} 
                    setUsername={setUsername} 
                    username={username} 
                    setPassword={setPassword} 
                    password={password} 
                    navigate={navigate}/>} 
                />
                <Route path='/login' element={<Login 
                    open={open} 
                    setOpen={setOpen} 
                    setUserProfile={setUserProfile} 
                    setToken={setToken} 
                    setUsername={setUsername} 
                    username={username} 
                    setPassword={setPassword} 
                    password={password} 
                    navigate={navigate}/>} 
                />
            </Routes>
        </div>
    )
}



//new way to render when react is 18 or later
const container = document.querySelector('#container');
const root = ReactDOM.createRoot(container);
root.render(
    <BrowserRouter>
            <App />
    </BrowserRouter>);
