import React, {useState} from "react";
import { useParams } from "react-router-dom";
import { sendMessage } from "../api";

const Message = ({posts, token}) => {
    const [message, setMessage] = useState('')

    const { postID } = useParams();

    const [currentPost] =  posts.filter(post => post._id === postID ); //this returns array of 1 item
    const {title, description, location, price, willDeliver} = currentPost;

    const handleSubmit = async() => {
        const results = await loginUser(username, password);

        if (results.success) {
            setToken(results.data.token)
            window.localStorage.setItem('token', results.data.token) 
            const profileResults = await getUserProfile(results.data.token)
            let myProfile = profileResults.data
            setUserProfile(myProfile)
            navigate('/profile') 
        } else {
            setOpen(true)
            let form = document.querySelector('form')
            form.reset()
        }
    }

    return (
        <main>
            <div className='Post'>
                <h3>{title}</h3>
                <p><strong>Description: </strong>{description}</p>
                <p><strong>Price:</strong> {price}</p>
                <p><strong>Location:</strong> {location}</p>
                {willDeliver ? (<p>Delivery: Yes</p>) : <p>Delivery: No</p>}
            </div>
            <form onSubmit = {(event)=> {
            event.preventDefault();
                handleSubmit()
            }}>
            <h3>Message User About This Post</h3>
            <input type='text' placeholder="type message here" onChange={event => setMessage(event.target.value)}></input>
            <button type='submit'>Send</button>
            </form>
        </main>

    )
}

export default Message;

const SinglePostView = ({posts}) => {
    const { postID } = useParams();

    const [currentPost] =  posts.filter(post => post._id === postID ); //this returns array of 1 item
    const {title, description, location, price, willDeliver} = currentPost;

    return (
            <div className='Post'>
                <h3>{title}</h3>
                <p><strong>Description: </strong>{description}</p>
                <p><strong>Price:</strong> {price}</p>
                <p><strong>Location:</strong> {location}</p>
                {willDeliver ? (<p>Delivery: Yes</p>) : <p>Delivery: No</p>}
            </div>

    )
}
