import React, { useState} from "react";
import './Posts.css'

const Posts = (props) => {
    const {posts} = props
    const [searchTerm, setSearchTerm] = useState('')

    const searchPost = (term) => {
        posts.map((post) => {
            const {description, location, price, title, _id} = post;
            if (description.includes(term) || title.includes(term)) {
                return <div key={_id} className='Post'>
                    <h3>{title}</h3>
                    <p>{description}</p>
                    <p><strong>Price:</strong> {price}</p>
                    <p><strong>Location:</strong> {location}</p>
                   </div>
            }
        })
    }

    // const postsToShow = () => {
    //     if (searchTerm !==)
    // }

        if (searchTerm) {
            return (
                <div className="postAndSearch">
                    <h1 className='postHeader'>POSTS</h1>
                    <form onSubmit = {(event)=> {
                        event.preventDefault();
                        searchPost(searchTerm)

                        }
                        }>
                        <input className='postSearchBar'
                        type='text'
                        placeholder="search"
                        onChange = {(event) => setSearchTerm(event.target.value)}
                        ></input>
                        <button type='submit'>Search</button>
                    </form>
                </div> 
            )
        } else {
            return (
                <div className='postMain'>
                    <div className="postAndSearch">
                        <h1 className='postHeader'>POSTS</h1>
                        <form onSubmit = {(event)=> {
                            event.preventDefault();
                            searchPost(searchTerm)

                            }
                            }>
                            <input className='postSearchBar'
                            type='text'
                            placeholder="search"
                            onChange = {(event) => setSearchTerm(event.target.value)}
                            ></input>
                            <button type='submit'>Search</button>
                        </form>
                    </div>

            {posts.map(post => {
                    const {description, location, price, title, _id} = post;
                    return <div key={_id} className='Post'>
                            <h3>{title}</h3>
                            <p>{description}</p>
                            <p><strong>Price:</strong> {price}</p>
                            <p><strong>Location:</strong> {location}</p>
                        </div>}
                )}
             </div>
            )
        }
    }


export default Posts;
